import React from 'react'
import '../02-useEffect/effects.css'
import { useCounter } from '../../hooks/useCounter'

export const Memorize = () => {

   const { counter, increment} = useCounter( 10 )

  return (
    <div>
        <h1>Counter: <small>{counter}</small></h1>
        <hr/>

        <button>
            +1
        </button>
    </div>
  )
}
