import { useState } from "react"

export const useCounter = ( initialState = 10 ) => {

    const [state, setState] = useState(initialState)

    const increment = (factor = 1) =>{
        setState( state + factor );
    }

    const decrement = () =>{
        setState( state - 1 );
    }

    return {
        state,
        increment,
        decrement
    };
}
